SELECT s.name as Site, rv.name as Vault, rf.name as Folder, count(*) as Objects FROM ((( (SELECT IDA3A4 FROM MASTEREDONREPLICAITEM MR WHERE STREAMID IN ( (SELECT DISTINCT streamid FROM masteredonreplicaitem MINUS SELECT DISTINCT streamid FROM applicationdata) )) MR INNER JOIN REPLICAFOLDER RF ON RF.IDA2A2=MR.IDA3A4) INNER JOIN REPLICAVAULT RV ON RV.IDA2A2=RF.IDA3A5) INNER JOIN site s ON s.ida2a2=rv.ida3a5 ) GROUP BY rf.name, rv.name, s.name ORDER BY s.name, rv.name, rf.name;